export type AlignmentOptionType = 'start' | 'center' | 'end' | number

export type AlignmentType = {
  measure: (n: number) => number
}

export function Alignment(
  align: AlignmentOptionType,
  viewSize: number,
): AlignmentType {
  const predefined = { start, center, end }

  function start(): number {
    return 0
  }

  function center(n: number): number {
    return end(n) / 2
  }

  function end(n: number): number {
    return viewSize - n
  }

  function percent(): number {
    return viewSize * Number(align)
  }

  function measure(n: number): number {
    if (typeof align === 'number') return percent()
    return predefined[align](n)
  }

  const self: AlignmentType = {
    measure,
  }
  return self
}
type CallbackType = () => void

export type AnimationType = {
  start: () => void
  stop: () => void
  proceed: () => void
}

export function Animation(callback: FrameRequestCallback): AnimationType {
  let animationFrame = 0

  function ifAnimating(active: boolean, cb: CallbackType): CallbackType {
    return (): void => {
      if (active === !!animationFrame) cb()
    }
  }

  function start(): void {
    animationFrame = window.requestAnimationFrame(callback)
  }

  function stop(): void {
    window.cancelAnimationFrame(animationFrame)
    animationFrame = 0
  }

  const self: AnimationType = {
    proceed: ifAnimating(true, start),
    start: ifAnimating(false, start),
    stop: ifAnimating(true, stop),
  }
  return self
}
import { DirectionOptionType } from './direction'

export type AxisOptionType = 'x' | 'y'
type AxisEdgeType = 'top' | 'right' | 'bottom' | 'left'

export type AxisType = {
  scroll: AxisOptionType
  cross: AxisOptionType
  startEdge: AxisEdgeType
  endEdge: AxisEdgeType
