}

export function Axis(
  axis: AxisOptionType,
  contentDirection: DirectionOptionType,
): AxisType {
  const scroll = axis === 'y' ? 'y' : 'x'
  const cross = axis === 'y' ? 'x' : 'y'
  const startEdge = getStartEdge()
  const endEdge = getEndEdge()

  function measureSize(rect: DOMRect): number {
    const { width, height } = rect
    return scroll === 'x' ? width : height
  }

  function getStartEdge(): AxisEdgeType {
    if (scroll === 'y') return 'top'
    return contentDirection === 'rtl' ? 'right' : 'left'
  }

  function getEndEdge(): AxisEdgeType {
    if (scroll === 'y') return 'bottom'
    return contentDirection === 'rtl' ? 'left' : 'right'
  }

  const self: AxisType = {
    scroll,
    cross,
    startEdge,
    endEdge,
    measureSize,
  }
  return self
}
export type LimitType = {
  min: number
  max: number
  length: number
  constrain: (n: number) => number
  reachedAny: (n: number) => boolean
  reachedMax: (n: number) => boolean
