  removeOffset: (n: number) => number
}

export function Limit(min: number, max: number): LimitType {
  const length = Math.abs(min - max)

  function reachedMin(n: number): boolean {
    return n < min
  }

  function reachedMax(n: number): boolean {
    return n > max
  }

  function reachedAny(n: number): boolean {
    return reachedMin(n) || reachedMax(n)
  }

  function constrain(n: number): number {
    if (!reachedAny(n)) return n
    return reachedMin(n) ? min : max
  }

  function removeOffset(n: number): number {
    if (!length) return n
    return n - length * Math.ceil((n - max) / length)
  }

  const self: LimitType = {
    constrain,
    length,
    max,
    min,
    reachedAny,
    reachedMax,
    reachedMin,
    removeOffset,
  }
  return self
}
import { Limit } from './limit'

export type CounterType = {
  get: () => number
  set: (n: number) => CounterType
  add: (n: number) => CounterType
  clone: () => CounterType
  min: number
  max: number
}

export function Counter(
  max: number,
  start: number,
  loop: boolean,
): CounterType {
  const { min, constrain } = Limit(0, max)
  const loopEnd = max + 1
  let counter = withinLimit(start)

  function withinLimit(n: number): number {
    return !loop ? constrain(n) : Math.abs((loopEnd + n) % loopEnd)
  }

