    return counter
  }

  function set(n: number): CounterType {
    counter = withinLimit(n)
    return self
  }

  function add(n: number): CounterType {
    return set(get() + n)
  }

  function clone(): CounterType {
    return Counter(max, get(), loop)
  }

  const self: CounterType = {
    add,
    clone,
    get,
    set,
    min,
    max,
  }
  return self
}
export type DirectionOptionType = 'ltr' | 'rtl'

export type DirectionType = {
  applyTo: (n: number) => number
}

export function Direction(direction: DirectionOptionType): DirectionType {
  const sign = direction === 'rtl' ? -1 : 1

  function applyTo(n: number): number {
    return n * sign
  }

  const self: DirectionType = {
    applyTo,
  }
  return self
}
