}
export type Vector1DType = {
  get: () => number
  set: (v: Vector1DType | number) => Vector1DType
  add: (v: Vector1DType | number) => Vector1DType
  subtract: (v: Vector1DType | number) => Vector1DType
  multiply: (n: number) => Vector1DType
  divide: (n: number) => Vector1DType
  normalize: () => Vector1DType
}

export function Vector1D(value: number): Vector1DType {
  let vector = value

  function get(): number {
    return vector
  }

  function set(n: Vector1DType | number): Vector1DType {
    vector = readNumber(n)
    return self
  }

  function add(n: Vector1DType | number): Vector1DType {
    vector += readNumber(n)
    return self
  }

  function subtract(n: Vector1DType | number): Vector1DType {
    vector -= readNumber(n)
    return self
  }

  function multiply(n: number): Vector1DType {
    vector *= n
    return self
  }

  function divide(n: number): Vector1DType {
    vector /= n
    return self
  }

  function normalize(): Vector1DType {
    if (vector !== 0) divide(vector)
    return self
  }

  function readNumber(n: Vector1DType | number): number {
    return typeof n === 'number' ? n : n.get()
  }

  const self: Vector1DType = {
    add,
    divide,
    get,
    multiply,
    normalize,
    set,
    subtract,
  }
  return self
}
export function map(
  value: number,
  iStart: number,
  iStop: number,
  oStart: number,
  oStop: number,
): number {
  return oStart + (oStop - oStart) * ((value - iStart) / (iStop - iStart))
}

export function mathSign(n: number): number {
  return !n ? 0 : n / Math.abs(n)
}

export function deltaAbs(valueB: number, valueA: number): number {
  return Math.abs(valueB - valueA)
}

export function factorAbs(valueB: number, valueA: number): number {
  if (valueB === 0 || valueA === 0) return 0
  if (Math.abs(valueB) <= Math.abs(valueA)) return 0
  const diff = deltaAbs(Math.abs(valueB), Math.abs(valueA))
  return Math.abs(diff / valueB)
}

export function roundToDecimals(decimalPoints: number): (n: number) => number {
  const pow = Math.pow(10, decimalPoints)
  return (n: number): number => Math.round(n * pow) / pow
}

export function debounce(callback: () => void, time: number): () => void {
  let timeout = 0
  return (): void => {
    window.clearTimeout(timeout)
    timeout = window.setTimeout(callback, time) || 0
  }
}

export function groupArray<GenericType>(
  array: GenericType[],
  size: number,
): GenericType[][] {
  const groups = []
  for (let i = 0; i < array.length; i += size) {
    groups.push(array.slice(i, i + size))
  }
  return groups
}

export function arrayKeys<GenericType>(array: GenericType[]): number[] {
  return Object.keys(array).map(Number)
}

export function arrayLast<GenericType>(array: GenericType[]): GenericType {
  return array[lastIndex(array)]
}

export function lastIndex<GenericType>(array: GenericType[]): number {
  return Math.max(0, array.length - 1)
}

export function removeClass(node: HTMLElement, className: string): void {
  const cl = node.classList
  if (className && cl.contains(className)) cl.remove(className)
}

export function addClass(node: HTMLElement, className: string): void {
  const cl = node.classList
  if (className && !cl.contains(className)) cl.add(className)
}
