}

export function SlideLooper(
  axis: AxisType,
  viewSize: number,
  contentSize: number,
  slideSizesWithGaps: number[],
  scrollSnaps: number[],
  slidesInView: SlidesInViewType,
  scrollLocation: Vector1DType,
  slides: HTMLElement[],
): SlideLooperType {
  const ascItems = arrayKeys(slideSizesWithGaps)
  const descItems = arrayKeys(slideSizesWithGaps).reverse()
  const loopPoints = startPoints().concat(endPoints())

  function removeSlideSizes(indexes: number[], from: number): number {
    return indexes.reduce((a: number, i) => {
      return a - slideSizesWithGaps[i]
    }, from)
  }

  function slidesInGap(indexes: number[], gap: number): number[] {
    return indexes.reduce((a: number[], i) => {
      const remainingGap = removeSlideSizes(a, gap)
      return remainingGap > 0 ? a.concat([i]) : a
    }, [])
  }

  function findLoopPoints(indexes: number[], edge: EdgeType): LoopPointType[] {
    const isStartEdge = edge === 'start'
    const offset = isStartEdge ? -contentSize : contentSize
    const slideBounds = slidesInView.findSlideBounds(offset)

    return indexes.map((index) => {
      const initial = isStartEdge ? 0 : -contentSize
      const altered = isStartEdge ? contentSize : 0
      const bounds = slideBounds.filter((b) => b.index === index)[0]
      const point = bounds[isStartEdge ? 'end' : 'start']
      const getTarget = (): number =>
        scrollLocation.get() > point ? initial : altered
      return { point, getTarget, index, location: -1 }
    })
  }

  function startPoints(): LoopPointType[] {
    const gap = scrollSnaps[0] - 1
    const indexes = slidesInGap(descItems, gap)
    return findLoopPoints(indexes, 'end')
  }

  function endPoints(): LoopPointType[] {
    const gap = viewSize - scrollSnaps[0] - 1
    const indexes = slidesInGap(ascItems, gap)
    return findLoopPoints(indexes, 'start')
  }

  function canLoop(): boolean {
    return loopPoints.every(({ index }) => {
      const otherIndexes = ascItems.filter((i) => i !== index)
      return removeSlideSizes(otherIndexes, viewSize) <= 0
    })
  }

  function loop(): void {
    loopPoints.forEach((loopPoint) => {
      const { getTarget, location, index } = loopPoint
      const target = getTarget()
      if (target !== location) {
        slides[index].style[axis.startEdge] = `${target}%`
        loopPoint.location = target
      }
    })
  }

  function clear(): void {
    loopPoints.forEach(({ index }) => {
      slides[index].style[axis.startEdge] = ''
    })
  }

  const self: SlideLooperType = {
    canLoop,
    clear,
    loop,
    loopPoints,
  }
  return self
}
import { EventStore, EventStoreType } from './eventStore'
import { ScrollToType } from './scrollTo'

export type SlideFocusType = {
  addActivationEvents: (slides: HTMLElement[]) => void
  removeAllEvents: EventStoreType['removeAll']
}

export function SlideFocus(
  rootNode: HTMLElement,
  scrollTo: ScrollToType,
  slidesToScroll: number,
): SlideFocusType {
  const eventStore = EventStore()
  const removeAllEvents = eventStore.removeAll
  let lastTabPressTime = 0

  function registerTabPress(event: Event): void {
    if ((event as KeyboardEvent).keyCode !== 9) return
    lastTabPressTime = new Date().getTime()
  }

  function addFocusEvent(slide: HTMLElement, index: number): void {
    const focus = (): void => {
      const nowTime = new Date().getTime()
      const diffTime = nowTime - lastTabPressTime
      if (diffTime > 10) return

      rootNode.scrollLeft = 0
      const selectedIndex = Math.floor(index / slidesToScroll)
      scrollTo.index(selectedIndex, 0)
    }
    eventStore.add(slide, 'focus', focus, true)
  }

  function addActivationEvents(slides: HTMLElement[]): void {
    eventStore.add(document, 'keydown', registerTabPress, false)
    slides.forEach(addFocusEvent)
  }

  const self: SlideFocusType = {
    addActivationEvents,
    removeAllEvents,
  }
  return self
}
type SlideBoundType = {
  start: number
  end: number
  index: number
}

export type SlidesInViewType = {
  check: (location: number) => number[]
  findSlideBounds: (offset: number, threshold?: number) => SlideBoundType[]
}

export function SlidesInView(
  viewSize: number,
  contentSize: number,
  slideSizes: number[],
  snaps: number[],
  loop: boolean,
  inViewThreshold: number,
): SlidesInViewType {
  const threshold = Math.min(Math.max(inViewThreshold, 0.01), 0.99)
  const offsets = loop ? [0, contentSize, -contentSize] : [0]
  const slideBounds = offsets.reduce((a: SlideBoundType[], offset) => {
    return a.concat(findSlideBounds(offset, threshold))
  }, [])

  function findSlideBounds(
    offset: number,
    threshold?: number,
  ): SlideBoundType[] {
    const thresholds = slideSizes.map((s) => s * (threshold || 0))
    return snaps.map((snap, index) => ({
      start: snap - slideSizes[index] + thresholds[index] + offset,
      end: snap + viewSize - thresholds[index] + offset,
      index,
    }))
  }

  function check(location: number): number[] {
    return slideBounds.reduce((list: number[], slideBound) => {
      const { index, start, end } = slideBound
      const inList = list.indexOf(index) !== -1
      const inView = start < location && end > location
      return !inList && inView ? list.concat([index]) : list
    }, [])
  }

  const self: SlidesInViewType = {
    check,
    findSlideBounds,
  }
  return self
}
import { AxisType } from './axis'
import { PxToPercentType } from './pxToPercent'
import { arrayLast, lastIndex } from './utils'

export type SlideSizesType = {
  slideSizes: number[]
  slideSizesWithGaps: number[]
}

export function SlideSizes(
  axis: AxisType,
  pxToPercent: PxToPercentType,
  slides: HTMLElement[],
  slideRects: DOMRect[],
  loop: boolean,
): SlideSizesType {
  const { measureSize, startEdge, endEdge } = axis
  const sizesInPx = slideRects.map(measureSize)
  const slideSizes = sizesInPx.map(pxToPercent.measure)
  const slideSizesWithGaps = measureWithGaps()

  function measureWithGaps(): number[] {
    return slideRects
      .map((rect, index, rects) => {
        const isLast = index === lastIndex(rects)
        const style = window.getComputedStyle(arrayLast(slides))
        const endGap = parseFloat(style.getPropertyValue(`margin-${endEdge}`))
        if (isLast) return sizesInPx[index] + (loop ? endGap : 0)
        return rects[index + 1][startEdge] - rect[startEdge]
      })
      .map(pxToPercent.measure)
      .map(Math.abs)
  }

  const self: SlideSizesType = {
    slideSizes,
    slideSizesWithGaps,
  }
  return self
}
import { AxisType } from './axis'
import { DirectionType } from './direction'
import { Vector1DType } from './vector1d'

export type TranslateType = {
  clear: () => void
  to: (vector: Vector1DType) => void
  toggleActive: (active: boolean) => void
}

export function Translate(
  axis: AxisType,
  direction: DirectionType,
  container: HTMLElement,
): TranslateType {
  const containerStyle = container.style
  const translate = axis.scroll === 'x' ? x : y
  let disabled = false

  function x(n: number): string {
    return `translate3d(${n}%,0px,0px)`
  }

  function y(n: number): string {
    return `translate3d(0px,${n}%,0px)`
  }

  function to(target: Vector1DType): void {
    if (disabled) return
    containerStyle.transform = translate(direction.applyTo(target.get()))
  }

  function toggleActive(active: boolean): void {
    disabled = !active
  }

  function clear(): void {
    containerStyle.transform = ''
  }

  const self: TranslateType = {
    clear,
    to,
    toggleActive,
  }
  return self
}
import { Alignment } from './alignment'
import { Animation, AnimationType } from './animation'
import { Axis, AxisType } from './axis'
import { Counter, CounterType } from './counter'
import { Direction, DirectionType } from './direction'
import { DragHandler, DragHandlerType } from './dragHandler'
import { DragTracker } from './dragTracker'
import { EventEmitterType } from './eventEmitter'
import { LimitType } from './limit'
import { OptionsType } from './options'
import { PxToPercent, PxToPercentType } from './pxToPercent'
import { ScrollBody, ScrollBodyType } from './scrollBody'
import { ScrollBounds, ScrollBoundsType } from './scrollBounds'
import { ScrollContain } from './scrollContain'
import { ScrollLimit } from './scrollLimit'
import { ScrollLooper, ScrollLooperType } from './scrollLooper'
import { ScrollProgress, ScrollProgressType } from './scrollProgress'
import { ScrollSnap } from './scrollSnap'
import { ScrollTarget, ScrollTargetType } from './scrollTarget'
